import { useState } from "react";
import {
  LayoutDashboard,
  Ticket,
  Users,
  LogOut,
  Plus,
  Search,
  CheckCircle,
  AlertCircle,
} from "lucide-react";

// --- CONFIGURATION ---
// Change this to your backend URL when running locally
const API_URL = "http://localhost:5000/api";

// --- MOCK DATA (For Demo/Preview purposes only) ---
const MOCK_TICKETS = [
  {
    _id: "1",
    title: "Laptop Overheating",
    description: "My Probook shuts down randomly.",
    status: "Open",
    priority: "High",
    createdBy: "john_doe",
    createdAt: new Date().toISOString(),
  },
  {
    _id: "2",
    title: "Software Install",
    description: "Need VS Code installed.",
    status: "Resolved",
    priority: "Low",
    createdBy: "jane_smith",
    createdAt: new Date().toISOString(),
  },
  {
    _id: "3",
    title: "Wifi Issues",
    description: "Cannot connect to 5G network.",
    status: "In Progress",
    priority: "Medium",
    createdBy: "john_doe",
    createdAt: new Date().toISOString(),
  },
];

const App = () => {
  // --- STATE ---
  const [user, setUser] = useState(null); // { username, role, token }
  const [view, setView] = useState("login"); // login, dashboard
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Forms
  const [loginForm, setLoginForm] = useState({ username: "", password: "" });
  const [newTicket, setNewTicket] = useState({
    title: "",
    description: "",
    priority: "Medium",
  });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filterStatus, setFilterStatus] = useState("All");

  // --- API HELPERS ---
  // This wrapper handles the difference between the Live Preview (Mock) and Real Backend
  const apiCall = async (endpoint, method = "GET", body = null) => {
    try {
      // IF we are in the browser preview environment, strictly use Mock Data
      // Remove this if-block when running locally to connect to real backend
      if (window.location.hostname.includes("container")) {
        console.log("Simulating API Call:", endpoint);
        if (endpoint === "/auth/login")
          return {
            token: "mock-jwt",
            role: body.username.includes("admin") ? "admin" : "user",
            username: body.username,
          };
        if (endpoint === "/tickets" && method === "GET") return MOCK_TICKETS;
        if (endpoint === "/tickets" && method === "POST") {
          const t = {
            ...body,
            _id: Math.random().toString(),
            status: "Open",
            createdBy: user.username,
            createdAt: new Date().toISOString(),
          };
          setTickets((prev) => [t, ...prev]);
          return t;
        }
        if (method === "PUT") {
          const updated = { ...body };
          setTickets((prev) =>
            prev.map((t) => (t._id === body._id ? updated : t))
          );
          return updated;
        }
      }

      // REAL BACKEND LOGIC
      const headers = { "Content-Type": "application/json" };
      if (user?.token) headers["Authorization"] = `Bearer ${user.token}`;

      const res = await fetch(`${API_URL}${endpoint}`, {
        method,
        headers,
        body: body ? JSON.stringify(body) : null,
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "API Error");
      return data;
    } catch (err) {
      // Fallback to mock if backend is offline (Design for Resilience)
      console.warn("Backend unreachable, using local logic:", err);
      setError("Backend unreachable. Showing cached/mock data.");
      if (endpoint === "/tickets" && method === "GET")
        return tickets.length ? tickets : MOCK_TICKETS;
      throw err;
    }
  };

  // --- HANDLERS ---

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      const data = await apiCall("/auth/login", "POST", loginForm);
      setUser(data);
      setView("dashboard");
      fetchTickets(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchTickets = async (currentUser) => {
    try {
      // Pass user manually as state might not have updated yet
      const usr = currentUser || user;
      const data = await apiCall("/tickets", "GET");
      setTickets(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleCreateTicket = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await apiCall("/tickets", "POST", newTicket);
      setIsModalOpen(false);
      setNewTicket({ title: "", description: "", priority: "Medium" });
      fetchTickets();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const updateStatus = async (id, newStatus) => {
    try {
      const ticketToUpdate = tickets.find((t) => t._id === id);
      await apiCall(`/tickets/${id}`, "PUT", {
        ...ticketToUpdate,
        status: newStatus,
      });
      fetchTickets();
    } catch (err) {
      setError("Failed to update status");
    }
  };

  // --- COMPONENTS ---

  const StatusBadge = ({ status }) => {
    const styles = {
      Open: "bg-blue-100 text-blue-800",
      "In Progress": "bg-yellow-100 text-yellow-800",
      Resolved: "bg-green-100 text-green-800",
    };
    return (
      <span
        className={`px-2 py-1 rounded-full text-xs font-semibold ${
          styles[status] || "bg-gray-100"
        }`}
      >
        {status}
      </span>
    );
  };

  const PriorityBadge = ({ priority }) => {
    const styles = {
      High: "text-red-600 font-bold",
      Medium: "text-orange-500 font-medium",
      Low: "text-gray-500",
    };
    return <span className={styles[priority]}>{priority}</span>;
  };

  // --- RENDER ---

  if (view === "login") {
    return (
      <div className="min-h-screen bg-slate-100 flex items-center justify-center p-4">
        <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-md">
          <div className="flex justify-center mb-6">
            <div className="bg-indigo-600 p-3 rounded-lg">
              <Ticket className="text-white h-8 w-8" />
            </div>
          </div>
          <h2 className="text-2xl font-bold text-center text-slate-800 mb-2">
            Proxima CRM
          </h2>
          <p className="text-center text-slate-500 mb-6">Helpdesk Portal</p>

          {error && (
            <div className="bg-red-50 text-red-600 p-3 rounded-lg mb-4 text-sm">
              {error}
            </div>
          )}

          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">
                Username
              </label>
              <input
                type="text"
                className="w-full p-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none transition"
                placeholder="e.g., admin or user"
                value={loginForm.username}
                onChange={(e) =>
                  setLoginForm({ ...loginForm, username: e.target.value })
                }
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1">
                Password
              </label>
              <input
                type="password"
                className="w-full p-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-indigo-500 outline-none transition"
                placeholder="Any password works"
                value={loginForm.password}
                onChange={(e) =>
                  setLoginForm({ ...loginForm, password: e.target.value })
                }
                required
              />
            </div>
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 rounded-lg transition duration-200"
            >
              {loading ? "Accessing..." : "Login System"}
            </button>
          </form>
          <div className="mt-4 text-center text-xs text-slate-400">
            Try username "admin" for Admin rights
          </div>
        </div>
      </div>
    );
  }

  // DASHBOARD VIEW
  const filteredTickets = tickets.filter(
    (t) => filterStatus === "All" || t.status === filterStatus
  );
  const stats = {
    total: tickets.length,
    open: tickets.filter((t) => t.status === "Open").length,
    resolved: tickets.filter((t) => t.status === "Resolved").length,
  };

  return (
    <div className="min-h-screen bg-slate-50 flex flex-col md:flex-row">
      {/* Sidebar */}
      <div className="w-full md:w-64 bg-slate-900 text-white p-6 flex flex-col">
        <div className="flex items-center gap-3 mb-10">
          <div className="bg-indigo-500 p-2 rounded-lg">
            <LayoutDashboard className="h-6 w-6" />
          </div>
          <span className="font-bold text-xl tracking-wide">Proxima</span>
        </div>

        <nav className="space-y-2 flex-1">
          <button className="flex items-center gap-3 w-full p-3 bg-slate-800 rounded-lg text-indigo-300">
            <Ticket size={20} /> Tickets
          </button>
          <button className="flex items-center gap-3 w-full p-3 hover:bg-slate-800 rounded-lg text-slate-400 transition">
            <Users size={20} /> {user.role === "admin" ? "Agents" : "Profile"}
          </button>
        </nav>

        <div className="pt-6 border-t border-slate-700">
          <div className="mb-4">
            <p className="text-sm text-slate-400">Logged in as</p>
            <p className="font-bold">{user.username}</p>
            <p className="text-xs text-indigo-400 uppercase">{user.role}</p>
          </div>
          <button
            onClick={() => {
              setUser(null);
              setView("login");
            }}
            className="flex items-center gap-2 text-red-400 hover:text-red-300 transition text-sm"
          >
            <LogOut size={16} /> Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-4 md:p-8 overflow-y-auto">
        {/* Header */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8 gap-4">
          <div>
            <h1 className="text-2xl font-bold text-slate-800">
              Helpdesk Overview
            </h1>
            <p className="text-slate-500">Manage and track support requests</p>
          </div>
          <button
            onClick={() => setIsModalOpen(true)}
            className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg shadow transition"
          >
            <Plus size={18} /> New Ticket
          </button>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
          <div className="bg-white p-4 rounded-xl shadow-sm border border-slate-100 flex items-center gap-4">
            <div className="p-3 bg-blue-50 text-blue-600 rounded-lg">
              <Ticket />
            </div>
            <div>
              <p className="text-slate-500 text-sm">Total Tickets</p>
              <p className="text-2xl font-bold">{stats.total}</p>
            </div>
          </div>
          <div className="bg-white p-4 rounded-xl shadow-sm border border-slate-100 flex items-center gap-4">
            <div className="p-3 bg-orange-50 text-orange-600 rounded-lg">
              <AlertCircle />
            </div>
            <div>
              <p className="text-slate-500 text-sm">Pending</p>
              <p className="text-2xl font-bold">{stats.open}</p>
            </div>
          </div>
          <div className="bg-white p-4 rounded-xl shadow-sm border border-slate-100 flex items-center gap-4">
            <div className="p-3 bg-green-50 text-green-600 rounded-lg">
              <CheckCircle />
            </div>
            <div>
              <p className="text-slate-500 text-sm">Resolved</p>
              <p className="text-2xl font-bold">{stats.resolved}</p>
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-t-xl p-4 border-b border-slate-100 flex items-center gap-4">
          <Search className="text-slate-400" size={20} />
          <input
            placeholder="Search tickets..."
            className="bg-transparent outline-none text-slate-600 flex-1"
          />
          <select
            className="bg-slate-50 border border-slate-200 rounded-lg p-2 text-sm outline-none"
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
          >
            <option value="All">All Status</option>
            <option value="Open">Open</option>
            <option value="In Progress">In Progress</option>
            <option value="Resolved">Resolved</option>
          </select>
        </div>

        {/* Ticket List */}
        <div className="bg-white rounded-b-xl shadow-sm border border-slate-100 overflow-hidden">
          <table className="w-full text-left">
            <thead className="bg-slate-50 text-slate-500 text-sm">
              <tr>
                <th className="p-4">Ticket Details</th>
                <th className="p-4">Priority</th>
                <th className="p-4">Status</th>
                <th className="p-4">Created By</th>
                <th className="p-4">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-100">
              {filteredTickets.map((ticket) => (
                <tr key={ticket._id} className="hover:bg-slate-50 transition">
                  <td className="p-4">
                    <p className="font-bold text-slate-800">{ticket.title}</p>
                    <p className="text-sm text-slate-500 truncate max-w-xs">
                      {ticket.description}
                    </p>
                  </td>
                  <td className="p-4">
                    <PriorityBadge priority={ticket.priority} />
                  </td>
                  <td className="p-4">
                    <StatusBadge status={ticket.status} />
                  </td>
                  <td className="p-4 text-sm text-slate-600">
                    {ticket.createdBy}
                  </td>
                  <td className="p-4">
                    {user.role === "admin" && (
                      <select
                        className="text-xs border rounded p-1 bg-white"
                        value={ticket.status}
                        onChange={(e) =>
                          updateStatus(ticket._id, e.target.value)
                        }
                      >
                        <option>Open</option>
                        <option>In Progress</option>
                        <option>Resolved</option>
                      </select>
                    )}
                    {user.role !== "admin" && (
                      <span className="text-xs text-slate-400">Read Only</span>
                    )}
                  </td>
                </tr>
              ))}
              {filteredTickets.length === 0 && (
                <tr>
                  <td colSpan="5" className="p-8 text-center text-slate-400">
                    No tickets found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* New Ticket Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl p-6 w-full max-w-lg shadow-2xl">
            <h3 className="text-xl font-bold mb-4">Create New Ticket</h3>
            <form onSubmit={handleCreateTicket} className="space-y-4">
              <div>
                <label className="block text-sm font-bold text-slate-700 mb-1">
                  Issue Title
                </label>
                <input
                  className="w-full border p-2 rounded focus:ring-2 focus:ring-indigo-500 outline-none"
                  value={newTicket.title}
                  onChange={(e) =>
                    setNewTicket({ ...newTicket, title: e.target.value })
                  }
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-bold text-slate-700 mb-1">
                  Priority
                </label>
                <select
                  className="w-full border p-2 rounded outline-none"
                  value={newTicket.priority}
                  onChange={(e) =>
                    setNewTicket({ ...newTicket, priority: e.target.value })
                  }
                >
                  <option>Low</option>
                  <option>Medium</option>
                  <option>High</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-bold text-slate-700 mb-1">
                  Description
                </label>
                <textarea
                  className="w-full border p-2 rounded h-24 focus:ring-2 focus:ring-indigo-500 outline-none"
                  value={newTicket.description}
                  onChange={(e) =>
                    setNewTicket({ ...newTicket, description: e.target.value })
                  }
                  required
                ></textarea>
              </div>
              <div className="flex justify-end gap-3 mt-6">
                <button
                  type="button"
                  onClick={() => setIsModalOpen(false)}
                  className="px-4 py-2 text-slate-600 hover:bg-slate-100 rounded transition"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
                >
                  Submit Ticket
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
